INSERT INTO `topic_reply` VALUES ('5be4256e21d75b74609f580a', '5be2b2c121d75b74609f50d8', '2018111306343287815318329816', '<div class=\"markdown-text\"><p><a href=\"/user/yinxin630\">@yinxin630</a> 若服务器支持跨域，  通过  node-fetch 可以直接拿到的 。\n<a href=\"https://github.com/waitingsong/rxxfetch/blob/master/test/30_cookie.test.ts#L72\">https://github.com/waitingsong/rxxfetch/blob/master/test/30_cookie.test.ts#L72</a></p>\n</div>', '5be2cc44646a05745b7b972d', '2018-11-08 12:00:46.938000', '2018-11-08 12:00:46.938000', '0');
